import React, { Component } from 'react';
import { Prism as SyntaxHighlighter } from 'react-syntax-highlighter';
import { atomDark } from 'react-syntax-highlighter/dist/styles/prism';
import DefaultPostlayout from '../../molecules/defaultPostLayout/DefaultPostLayout';
import styles from './commonPostStyles.module.scss';

class PostItem extends Component {
	constructor(props) {
		super(props);
		this.state = { 
			content: null,
		}
	}

	componentDidMount(){
		this.content()
	}

	handleBlogName = () => {
		this.props.history.push("/")
	}

	content = () => {
		const codeString = 
`let domContainer = document.getElementById('container'); 
// Clear the tree domContainer.innerHTML = ''; 
// Create the new host instance tree 
let domNode = document.createElement('button'); 
domNode.className = 'red'; 
domContainer.appendChild(domNode);`;

		const mainContent = (
			<div className={styles.commonPStyle}>
				<p>Most tutorials introduce React as a UI library. This makes sense because React is a UI library. That’s literally what the tagline says!</p>
				<hr />
				<p><strong>This post won’t teach you anything about creating user interfaces.</strong>But it might help you understand the React programming model in more depth.</p>
				<hr />
				<p>Most tutorials introduce React as a UI library. This makes sense because React is a UI library. That’s literally what the tagline says!</p>
				<SyntaxHighlighter language='javascript' style={atomDark}>{codeString}</SyntaxHighlighter>
			</div>
		)
		this.setState({
			content: mainContent
		})
	}

	render() { 
		const { content } = this.state;

		return ( 
				<DefaultPostlayout
					postTitle={'Preparing for a Tech Talk, Part 1: Motivation'}
					postDate={'December 26, 2018'}
					content={content}
					handleBlogName={this.handleBlogName}
				/>
		);
	}
}

export default PostItem;
