import React, { Component } from 'react';
import { Prism as SyntaxHighlighter } from 'react-syntax-highlighter';
import { darcula } from 'react-syntax-highlighter/dist/styles/prism';
import DefaultPostlayout from '../../molecules/defaultPostLayout/DefaultPostLayout';

const mobileQuery = window.matchMedia( "(max-width: 768px)" );
const tabletQuery = window.matchMedia( "(max-width: 1280px)" );


class PostItem extends Component {
	constructor(props) {
		super(props);
		this.state = { 
			content: null,
		}
	}

	componentDidMount(){
		this.content()
	}

	mediaSize = () => {
    mobileQuery.addListener(this.screenTestMobile)
    tabletQuery.addListener(this.screenTestTablet)

    let size = '';
    if (mobileQuery.matches) {
      size = '70px'
    } else if(tabletQuery.matches) {
      size = '60px'
    } else {
      size = '100px'
    }
    return size;
	}

	content = () => {
		const codeString = '(num) => num + 1;'
		const mainContent = (
			<div>
				<p>Most tutorials introduce React as a UI library. This makes sense because React is a UI library. That’s literally what the tagline says!</p>
				<hr />
				<p><strong>This post won’t teach you anything about creating user interfaces.</strong>But it might help you understand the React programming model in more depth.</p>
				<hr />
				<p>Most tutorials introduce React as a UI library. This makes sense because React is a UI library. That’s literally what the tagline says!</p>
				<SyntaxHighlighter language='javascript' style={darcula}>{codeString}</SyntaxHighlighter>;
			</div>
		)
		this.setState({
			content: mainContent
		})
	}

	render() { 
		const { content } = this.state;

		return ( 
				<DefaultPostlayout 
					blogName={'SpaceH'}
					postTitle={'Preparing for a Tech Talk, Part 1: Motivation Preparing for a Tech Talk, Part 1: Motivation'}
					postDate={'December 26, 2018'}
					size={this.mediaSize}
					content={content}
					topText={'Personal Blog by Harshit Prajapati'}
					bottomText={'Blog in progress'}
				/>
		);
	}
}

export default PostItem;
